INSERT INTO `h5p_libraries` VALUES(7, 'H5P.MultiChoice', 'Multiple Choice', 1, 14, 3, 1, 0, 'iframe', 'js/multichoice.js', 'css/multichoice.css', '', '[\n  {\n    \"name\": \"media\",\n    \"type\": \"group\",\n    \"label\": \"Media\",\n    \"importance\": \"medium\",\n    \"fields\": [\n      {\n        \"name\": \"type\",\n        \"type\": \"library\",\n        \"label\": \"Type\",\n        \"importance\": \"medium\",\n        \"options\": [\n          \"H5P.Image 1.1\",\n          \"H5P.Video 1.5\"\n        ],\n        \"optional\": true,\n        \"description\": \"Optional media to display above the question.\"\n      },\n      {\n        \"name\": \"disableImageZooming\",\n        \"type\": \"boolean\",\n        \"label\": \"Disable image zooming\",\n        \"importance\": \"low\",\n        \"default\": false,\n        \"optional\": true,\n        \"widget\": \"showWhen\",\n        \"showWhen\": {\n          \"rules\": [\n            {\n              \"field\": \"type\",\n              \"equals\": \"H5P.Image 1.1\"\n            }\n          ]\n        }\n      }\n    ]\n  },\n  {\n    \"name\": \"question\",\n    \"type\": \"text\",\n    \"importance\": \"medium\",\n    \"widget\": \"html\",\n    \"label\": \"Question\",\n    \"enterMode\": \"p\",\n    \"tags\": [\n      \"strong\",\n      \"em\",\n      \"sub\",\n      \"sup\",\n      \"h2\",\n      \"h3\",\n      \"pre\",\n      \"code\"\n    ]\n  },\n  {\n    \"name\": \"answers\",\n    \"type\": \"list\",\n    \"importance\": \"high\",\n    \"label\": \"Available options\",\n    \"entity\": \"option\",\n    \"min\": 1,\n    \"defaultNum\": 2,\n    \"field\": {\n      \"name\": \"answer\",\n      \"type\": \"group\",\n      \"label\": \"Option\",\n      \"importance\": \"high\",\n      \"fields\": [\n        {\n          \"name\": \"text\",\n          \"type\": \"text\",\n          \"importance\": \"medium\",\n          \"widget\": \"html\",\n          \"label\": \"Text\",\n          \"tags\": [\n            \"strong\",\n            \"em\",\n            \"sub\",\n            \"sup\",\n            \"code\"\n          ]\n        },\n        {\n          \"name\": \"correct\",\n          \"type\": \"boolean\",\n          \"label\": \"Correct\",\n          \"importance\": \"low\"\n        },\n        {\n          \"name\": \"tipsAndFeedback\",\n          \"type\": \"group\",\n          \"label\": \"Tips and feedback\",\n          \"importance\": \"low\",\n          \"optional\": true,\n          \"fields\": [\n            {\n              \"name\": \"tip\",\n              \"type\": \"text\",\n              \"widget\": \"html\",\n              \"label\": \"Tip text\",\n              \"importance\": \"low\",\n              \"description\": \"Hint for the user. This will appear before user checks his answer/answers.\",\n              \"optional\": true,\n              \"tags\": [\n                \"p\",\n                \"br\",\n                \"strong\",\n                \"em\",\n                \"a\",\n                \"code\"\n              ]\n            },\n            {\n              \"name\": \"chosenFeedback\",\n              \"type\": \"text\",\n              \"widget\": \"html\",\n              \"label\": \"Message displayed if answer is selected\",\n              \"importance\": \"low\",\n              \"description\": \"Message will appear below the answer on \\\"check\\\" if this answer is selected.\",\n              \"optional\": true,\n              \"tags\": [\n                \"strong\",\n                \"em\",\n                \"sub\",\n                \"sup\",\n                \"a\",\n                \"code\"\n              ]\n            },\n            {\n              \"name\": \"notChosenFeedback\",\n              \"type\": \"text\",\n              \"widget\": \"html\",\n              \"label\": \"Message displayed if answer is not selected\",\n              \"importance\": \"low\",\n              \"description\": \"Message will appear below the answer on \\\"check\\\" if this answer is not selected.\",\n              \"optional\": true,\n              \"tags\": [\n                \"strong\",\n                \"em\",\n                \"sub\",\n                \"sup\",\n                \"a\",\n                \"code\"\n              ]\n            }\n          ]\n        }\n      ]\n    }\n  },\n  {\n    \"name\": \"overallFeedback\",\n    \"type\": \"group\",\n    \"label\": \"Overall Feedback\",\n    \"importance\": \"low\",\n    \"expanded\": true,\n    \"fields\": [\n      {\n        \"name\": \"overallFeedback\",\n        \"type\": \"list\",\n        \"widgets\": [\n          {\n            \"name\": \"RangeList\",\n            \"label\": \"Default\"\n          }\n        ],\n        \"importance\": \"high\",\n        \"label\": \"Define custom feedback for any score range\",\n        \"description\": \"Click the \\\"Add range\\\" button to add as many ranges as you need. Example: 0-20% Bad score, 21-91% Average Score, 91-100% Great Score!\",\n        \"entity\": \"range\",\n        \"min\": 1,\n        \"defaultNum\": 1,\n        \"optional\": true,\n        \"field\": {\n          \"name\": \"overallFeedback\",\n          \"type\": \"group\",\n          \"importance\": \"low\",\n          \"fields\": [\n            {\n              \"name\": \"from\",\n              \"type\": \"number\",\n              \"label\": \"Score Range\",\n              \"min\": 0,\n              \"max\": 100,\n              \"default\": 0,\n              \"unit\": \"%\"\n            },\n            {\n              \"name\": \"to\",\n              \"type\": \"number\",\n              \"min\": 0,\n              \"max\": 100,\n              \"default\": 100,\n              \"unit\": \"%\"\n            },\n            {\n              \"name\": \"feedback\",\n              \"type\": \"text\",\n              \"label\": \"Feedback for defined score range\",\n              \"importance\": \"low\",\n              \"placeholder\": \"Fill in the feedback\",\n              \"optional\": true\n            }\n          ]\n        }\n      }\n    ]\n  },\n  {\n    \"name\": \"UI\",\n    \"type\": \"group\",\n    \"label\": \"User interface translations for multichoice\",\n    \"importance\": \"low\",\n    \"common\": true,\n    \"fields\": [\n      {\n        \"name\": \"checkAnswerButton\",\n        \"type\": \"text\",\n        \"label\": \"Check answer button label\",\n        \"importance\": \"low\",\n        \"default\": \"Check\"\n      },\n      {\n        \"name\": \"showSolutionButton\",\n        \"type\": \"text\",\n        \"label\": \"Show solution button label\",\n        \"importance\": \"low\",\n        \"default\": \"Show solution\"\n      },\n      {\n        \"name\": \"tryAgainButton\",\n        \"type\": \"text\",\n        \"label\": \"Retry button label\",\n        \"importance\": \"low\",\n        \"default\": \"Retry\",\n        \"optional\": true\n      },\n      {\n        \"name\": \"tipsLabel\",\n        \"type\": \"text\",\n        \"label\": \"Tip label\",\n        \"importance\": \"low\",\n        \"default\": \"Show tip\",\n        \"optional\": true\n      },\n      {\n        \"name\": \"scoreBarLabel\",\n        \"type\": \"text\",\n        \"label\": \"Textual representation of the score bar for those using a readspeaker\",\n        \"description\": \"Available variables are :num and :total\",\n        \"importance\": \"low\",\n        \"default\": \"You got :num out of :total points\",\n        \"optional\": true\n      },\n      {\n        \"name\": \"tipAvailable\",\n        \"type\": \"text\",\n        \"label\": \"Tip Available (not displayed)\",\n        \"importance\": \"low\",\n        \"default\": \"Tip available\",\n        \"description\": \"Accessibility text used for readspeakers\",\n        \"optional\": true\n      },\n      {\n        \"name\": \"feedbackAvailable\",\n        \"type\": \"text\",\n        \"label\": \"Feedback Available (not displayed)\",\n        \"importance\": \"low\",\n        \"default\": \"Feedback available\",\n        \"description\": \"Accessibility text used for readspeakers\",\n        \"optional\": true\n      },\n      {\n        \"name\": \"readFeedback\",\n        \"type\": \"text\",\n        \"label\": \"Read Feedback (not displayed)\",\n        \"importance\": \"low\",\n        \"default\": \"Read feedback\",\n        \"description\": \"Accessibility text used for readspeakers\",\n        \"optional\": true,\n        \"deprecated\": true\n      },\n      {\n        \"name\": \"wrongAnswer\",\n        \"type\": \"text\",\n        \"label\": \"Wrong Answer (not displayed)\",\n        \"importance\": \"low\",\n        \"default\": \"Wrong answer\",\n        \"description\": \"Accessibility text used for readspeakers\",\n        \"optional\": true,\n        \"deprecated\": true\n      },\n      {\n        \"name\": \"correctAnswer\",\n        \"type\": \"text\",\n        \"label\": \"Correct Answer (not displayed)\",\n        \"importance\": \"low\",\n        \"default\": \"Correct answer\",\n        \"description\": \"Accessibility text used for readspeakers\",\n        \"optional\": true\n      },\n      {\n        \"name\": \"shouldCheck\",\n        \"type\": \"text\",\n        \"label\": \"Option should have been checked\",\n        \"importance\": \"low\",\n        \"default\": \"Should have been checked\",\n        \"optional\": true\n      },\n      {\n        \"name\": \"shouldNotCheck\",\n        \"type\": \"text\",\n        \"label\": \"Option should not have been checked\",\n        \"importance\": \"low\",\n        \"default\": \"Should not have been checked\",\n        \"optional\": true\n      },\n      {\n        \"label\": \"Text for \\\"Requires answer\\\" message\",\n        \"importance\": \"low\",\n        \"name\": \"noInput\",\n        \"type\": \"text\",\n        \"default\": \"Please answer before viewing the solution\",\n        \"optional\": true\n      }\n    ]\n  },\n  {\n    \"name\": \"behaviour\",\n    \"type\": \"group\",\n    \"label\": \"Behavioural settings\",\n    \"importance\": \"low\",\n    \"description\": \"These options will let you control how the task behaves.\",\n    \"optional\": true,\n    \"fields\": [\n      {\n        \"name\": \"enableRetry\",\n        \"type\": \"boolean\",\n        \"label\": \"Enable \\\"Retry\\\" button\",\n        \"importance\": \"low\",\n        \"default\": true,\n        \"optional\": true\n      },\n      {\n        \"name\": \"enableSolutionsButton\",\n        \"type\": \"boolean\",\n        \"label\": \"Enable \\\"Show Solution\\\" button\",\n        \"importance\": \"low\",\n        \"default\": true,\n        \"optional\": true\n      },\n      {\n        \"name\": \"enableCheckButton\",\n        \"type\": \"boolean\",\n        \"label\": \"Enable \\\"Check\\\" button\",\n        \"widget\": \"none\",\n        \"importance\": \"low\",\n        \"default\": true,\n        \"optional\": true\n      },\n      {\n        \"name\": \"type\",\n        \"type\": \"select\",\n        \"label\": \"Question Type\",\n        \"importance\": \"low\",\n        \"description\": \"Select the look and behaviour of the question.\",\n        \"default\": \"auto\",\n        \"options\": [\n          {\n            \"value\": \"auto\",\n            \"label\": \"Automatic\"\n          },\n          {\n            \"value\": \"multi\",\n            \"label\": \"Multiple Choice (Checkboxes)\"\n          },\n          {\n            \"value\": \"single\",\n            \"label\": \"Single Choice (Radio Buttons)\"\n          }\n        ]\n      },\n      {\n        \"name\": \"singlePoint\",\n        \"type\": \"boolean\",\n        \"label\": \"Give one point for the whole task\",\n        \"importance\": \"low\",\n        \"description\": \"Enable to give a total of one point for multiple correct answers. This will not be an option in \\\"Single answer\\\" mode.\",\n        \"default\": false\n      },\n      {\n        \"name\": \"randomAnswers\",\n        \"type\": \"boolean\",\n        \"label\": \"Randomize answers\",\n        \"importance\": \"low\",\n        \"description\": \"Enable to randomize the order of the answers on display.\",\n        \"default\": true\n      },\n      {\n        \"label\": \"Require answer before the solution can be viewed\",\n        \"importance\": \"low\",\n        \"name\": \"showSolutionsRequiresInput\",\n        \"type\": \"boolean\",\n        \"default\": true,\n        \"optional\": true\n      },\n      {\n        \"label\": \"Show confirmation dialog on \\\"Check\\\"\",\n        \"importance\": \"low\",\n        \"name\": \"confirmCheckDialog\",\n        \"type\": \"boolean\",\n        \"default\": false\n      },\n      {\n        \"label\": \"Show confirmation dialog on \\\"Retry\\\"\",\n        \"importance\": \"low\",\n        \"name\": \"confirmRetryDialog\",\n        \"type\": \"boolean\",\n        \"default\": false\n      },\n      {\n        \"label\": \"Automatically check answers\",\n        \"importance\": \"low\",\n        \"name\": \"autoCheck\",\n        \"type\": \"boolean\",\n        \"default\": false,\n        \"description\": \"Enabling this option will make accessibility suffer, make sure you know what you\'re doing.\"\n      },\n      {\n        \"label\": \"Pass percentage\",\n        \"name\": \"passPercentage\",\n        \"type\": \"number\",\n        \"description\": \"This setting often won\'t have any effect. It is the percentage of the total score required for getting 1 point when one point for the entire task is enabled, and for getting result.success in xAPI statements.\",\n        \"min\": 0,\n        \"max\": 100,\n        \"step\": 1,\n        \"default\": 100\n      },\n      {\n        \"name\": \"showScorePoints\",\n        \"type\": \"boolean\",\n        \"label\": \"Show score points\",\n        \"description\": \"Show points earned for each answer. This will not be 