INSERT INTO `h5p_libraries_languages` VALUES(7, 'ar', '{\n  \"semantics\": [\n    {\n      \"label\": \"وسائط\",\n      \"fields\": [\n        {\n          \"label\": \"النوع\",\n          \"description\": \"وسائط اختيارية لعرضها فوق السؤال.\"\n        },\n        {\n          \"label\": \"تعطيل تكبير الصورة من اجل صورة السؤال\"\n        }\n      ]\n    },\n    {\n      \"label\": \"السؤال\"\n    },\n    {\n      \"label\": \"الخيارات المتاحة\",\n      \"entity\": \"option\",\n      \"field\": {\n        \"label\": \"خيار\",\n        \"fields\": [\n          {\n            \"label\": \"نص\"\n          },\n          {\n            \"label\": \"صح\"\n          },\n          {\n            \"label\": \"تلميحات وملاحظات\",\n            \"fields\": [\n              {\n                \"label\": \"نص التلميح\",\n                \"description\": \"تلميح للمستخدم. سيظهر هذا قبل أن يتحقق المستخدم من إجابته / إجاباته.\"\n              },\n              {\n                \"label\": \"الرسالة المعروضة في حال تم تحديد إجابة\",\n                \"description\": \"ستظهر الرسالة أسفل الإجابة  إذا تم تحديد هذه الإجابة.\"\n              },\n              {\n                \"label\": \"الرسالة المعروضة في حال عدم تحديد اجابة\",\n                \"description\": \"ستظهر الرسالة أسفل الإجابة إذا لم يتم تحديد هذه الإجابة.\"\n              }\n            ]\n          }\n        ]\n      }\n    },\n    {\n      \"label\": \"رد الفعل الاجمالي\",\n      \"fields\": [\n        {\n          \"widgets\": [\n            {\n              \"label\": \"افتراضي\"\n            }\n          ],\n          \"label\": \"تحديد الملاحظات المخصصة لأي مجال درجات\",\n          \"description\": \"انقر فوق الزر \\\" اضافةمجال \\\" لإضافة أكبر عدد ممكن من النطاقات.وكمثال: 0-20٪ درجة سيئة ، 21-91٪ درجة متوسطة ، 91-100٪ درجة رائعة!\",\n          \"entity\": \"range\",\n          \"field\": {\n            \"fields\": [\n              {\n                \"label\": \"مجال الدرجات\"\n              },\n              