INSERT INTO `h5p_libraries_languages` VALUES(7, 'hi', '{\n  \"semantics\": [\n    {\n      \"label\": \"साधन (मीडिया)\",\n      \"fields\": [\n        {\n          \"label\": \"प्रकार\",\n          \"description\": \"प्रश्न के ऊपर प्रदर्शित करने के लिए वैकल्पिक साधन (मीडिया)।\"\n        },\n        {\n          \"label\": \"छवि को बड़ा करना अक्षम करें\"\n        }\n      ]\n    },\n    {\n      \"label\": \"प्रश्न\"\n    },\n    {\n      \"label\": \"उपलब्ध विकल्प\",\n      \"entity\": \"विकल्प\",\n      \"field\": {\n        \"label\": \"विकल्प\",\n        \"fields\": [\n          {\n            \"label\": \"पाठ\"\n          },\n          {\n            \"label\": \"सही है\"\n          },\n          {\n            \"label\": \"सुझाव एवं प्रतिक्रिया\",\n            \"fields\": [\n              {\n                \"label\": \"सुझाव पाठ\",\n                \"description\": \"उपयोगकर्ता के लिए सहायक-सूचना। उपयोगकर्ता को यह उत्तर जांचने से पहले दिखाई देगी।\"\n              },\n              {\n                \"label\": \"उत्तर चयनित होने पर प्रदर्शित संदेश\",\n                \"description\": \"यदि यह उत्तर चयनित है तो यह संदेश \\\"जांचने\\\" पर उत्तर के नीचे दिखाई देगा।\"\n              },\n              {\n                \"label\": \"उत्तर चयनित नहीं होने पर प्रदर्शित संदेश\",\n                \"description\": \"यदि यह उत्तर चयनित नहीं है तो यह संदेश \\\"जांचने\\\" पर उत्तर के नीचे दिखाई देगा।\"\n              }\n            ]\n          }\n        ]\n      }\n    },\n    {\n      \"label\": \"समग्र प्रतिक्रिया\",\n      \"fields\": [\n        {\n          \"widgets\": [\n            {\n              \"label\": \"डिफॉल्ट\"\n            }\n          ],\n          \"label\": \"अंक विस्तार के लिए प्रतिक्रिया को परिभाषित करें\",\n          \"description\": \"उदाहरण: ०-२०% बहुत कम अंक, २१-९०% औसत अंक, ९१-१००% बहुत अच्छे अंक!\",\n          \"entity\": \"विस्तार\",\n          \"field\": {\n            \"fields\": [\n              {\n                \"label\": \"अंक विस्तार\"\n              },\n              {},\n              {\n                \"label\": \"परिभाषिता अंक विस्तार के लिए प्रतिक्रिया\",\n                \"placeholder\": \"प्रतिक्रिया भरें\"\n              }\n            ]\n          }\n        }\n      ]\n    },\n    {\n      \"label\": \"बहुविकल्पी प्रश्न के उपयोगकर्ता इंटरफेस का अनुवाद\",\n      \"fields\": [\n        {\n          \"label\": \"उत्तर जाँचने के बटन का नाम-पत्र\",\n          \"default\": \"जांचें\"\n        },\n        {\n          \"label\": \"समाधान दिखाने के बटन का नाम-पत्र\",\n          \"default\": \"समाधान दिखाएं\"\n        },\n        {\n          \"label\": \"पुन: प्रयास बटन का नाम-पत्र\",\n          \"default\": \"पुन: प्रयास करें\"\n        },\n        {\n          \"label\": \"सुझाव का नाम-पत्र\",\n          \"default\": \"सुझाव दिखाएं\"\n        },\n        {\n          \"label\": \"पटल पढ़ने वाले वक्ता का उपयोग करने वालों के लिए अंक पट्टी का शाब्दिक निरूपण\",\n          \"description\": \":num तथा :total उपलब्ध चर हैं\",\n          \"default\": \"आपको :total में से :num अंक मिले\"\n        },\n        {\n          \"label\": \"सुझाव उपलब्ध है (प्रदर्शित नहीं किया गया)\",\n          \"default\": \"सुझाव उपलब्ध है\",\n          \"description\": \"पटल पढ़ने वाले वक्ता के लिए प्रयुक्त अभिगम्यता पाठ\"\n        },\n        {\n          \"label\": \"प्रतिक्रिया उपलब्ध है (प्रदर्शित नहीं किया गया)\",\n          \"default\": \"प्रतिक्रिया उपलब्ध है\",\n          \"description\": \"पटल पढ़ने वाले वक्ता के लिए प्रयुक्त अभिगम्यता पाठ\"\n        },\n        {\n          \"label\": \"प्रतिक्रिया पढ़ें (प्रदर्शित नहीं किया गया)\",\n          \"default\": \"प्रतिक्रिया पढ़ें\",\n          \"description\": \"पटल पढ़ने वाले वक्ता के लिए प्रयुक्त अभिगम्यता पाठ\"\n        },\n        {\n          \"label\": \"गलत उत्तर (प्रदर्शित नहीं किया गया)\",\n          \"default\": \"गलत उत्तर\",\n          \"description\": \"पटल पढ़ने वाले वक्ता के लिए प्रयुक्त अभिगम्यता पाठ\"\n        },\n        {\n          \"label\": \"सही उत्तर (प्रदर्शित नहीं किया गया)\",\n          \"default\": \"सही उत्तर\",\n          \"description\": \"पटल पढ़ने वाले वक्ता के लिए प्रयुक्त अभिगम्यता पाठ\"\n        },\n        {\n          \"label\": \"विकल्प चुना जाना चाहिए था\",\n          \"default\": \"चुना जाना चाहिए था\"\n        },\n        {\n          \"label\": \"विकल्प नहीं चुना जाना चाहिए था\",\n          \"default\": \"नहीं चुना जाना चाहिए था\"\n        },\n        {\n          \"label\": \"\\\"उत्तर की आवश्यकता है\\\" संदेश के लिए पाठ\",\n          \"default\": \"कृपया समाधान देखने से पहले उत्तर दें\"\n        }\n      ]\n    },\n    {\n      \"label\": \"व्यवहार सम्बंधित विन्यास\",\n      \"description\": \"ये विकल्प आपको यह नियंत्रित करने देंगे कि कार्य कैसे व्यवहार करता है।\",\n      \"fields\": [\n        {\n          \"label\": \"\\\"पुन: प्रयास करें\\\" बटन सक्षम करें\"\n        },\n        {\n          \"label\": \"\\\"समाधान दिखाएं\\\" बटन सक्षम करें\"\n        },\n        {\n          \"label\": \"\\\"जांचें\\\" बटन सक्षम करें\"\n        },\n        {\n          \"label\": \"प्रश्न का प्रकार\",\n          \"description\": \"प्रश्न का रूप और व्यवहार चुनें।\",\n          \"options\": [\n            {\n              \"label\": \"स्वचालित\"\n            },\n            {\n              \"label\": \"बहुविकल्पी (चेक बॉक्स)\"\n            },\n            {\n              \"label\": \"एकल विकल्प (रेडियो बटन)\"\n            }\n          ]\n        },\n        {\n          \"label\": \"संपूर्ण कार्य के लिए एक अंक दें\",\n          \"description\": \"कई सही उत्तरों के लिए कुल एक अंक देने के लिए इसे सक्षम। \\\"एकल उत्तर\\\" प्रणाली में यह विकल्प उपलब्ध नहीं होगा।\"\n        },\n        {\n          \"label\": \"उत्तरों को अनियमित करें\",\n          \"description\": \"उत्तरों के क्रम को अनियमित करने के लिए सक्षम करें।\"\n        },\n        {\n          \"label\": \"समाधान देखने से पहले उत्तर की आवश्यकता है\"\n        },\n        {\n          \"label\": \"\\\"चयन करने पर\\\" पुष्टि संवाद दिखाएं\"\n        },\n        {\n          \"label\": \"\\\"पुन: प्रयास करने पर\\\" पुष्टि संवाद दिखाएं\"\n        },\n        {\n          \"label\": \"स्वचालित रूप से उत्तरों की जाँच करें\",\n          \"description\": \"इस विकल्प को सक्षम करने से अभिगम्यता में दिक्कत होगी। अत: आप क्या कर रहे हैं यह अच्छी तरह से समझ लें।\"\n        },\n        {\n          \"label\": \"उत्तीर्ण प्रतिशत\",\n          \"description\": \"जब संपूर्ण कार्य के लिए एक अंक देना सक्षम होता है तब यह १ अंक प्राप्त करने के लिए आवश्यक कुल अंकों का प्रतिशत होता है। तथा यह xAPI statements में result.success के लिए भी प्रयुक्त होता है।\"\n        },\n        {\n          \"label\": \"प्राप्तांक दिखाएं\",\n          \"description\": \"प्रत्येक उत्तर के लिए अर्जित अंक दिखाएं।\"\n        }\n      ]\n    },\n    {\n      \"label\": \"पुष्टि संवाद की जाँच\",\n      \"fields\": [\n        {\n          \"label\": \"शीर्षक पाठ\",\n          \"default\": \"समाप्त करें ?\"\n        },\n        {\n          \"label\": \"मुख्य पाठ\",\n          \"default\": \"क्या आप सचमुच समाप्त करना चाहते हैं ?\"\n        },\n        {\n          \"label\": \"रद्द बटन का लेबल\",\n          \"default\": \"रद्द करें\"\n        },\n        {\n          \"label\": \"पुष्टि बटन का लेबल\",\n          \"default\": \"समाप्त करें\"\n        }\n      ]\n    },\n    {\n      \"label\": \"पुन: पुष्टि संवाद\",\n      \"fields\": [\n        {\n          \"label\": \"शीर्षक पाठ\",\n          \"default\": \"पुन: प्रयास करें ?\"\n        },\n        {\n          \"label\": \"मुख्य पाठ\",\n          \"default\": \"क्या आप सचमुच पुन: प्रयास करना चाहते हैं ?\"\n        },\n        {\n          \"label\": \"रद्द बटन का लेबल\",\n          \"default\": \"रद्द करें\"\n        },\n        {\n          \"label\": \"पुष्टि बटन का लेबल\",\n          \"default\": \"पुष्टि करें\"\n        }\n      ]\n    }\n  ]\n}\n');
INSERT INTO `h5p_libraries_languages` VALUES(7, 'hu', '{\n  \"semantics\": [\n    {\n      \"label\": \"Media\",\n      \"fields\": [\n        {\n          \"label\": \"Type\",\n          \"description\": \"Optional media to display above the question.\"\n        },\n        {\n          \"label\": \"Disable image zooming\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Question\"\n    },\n    {\n      \"label\": \"Available options\",\n      \"entity\": \"option\",\n      \"field\": {\n        \"label\": \"Option\",\n        \"fields\": [\n          {\n            \"label\": \"Text\"\n          },\n          {\n            \"label\": \"Correct\"\n          },\n          {\n            \"label\": \"Tips and feedback\",\n            \"fields\": [\n              {\n                \"label\": \"Tip text\",\n                \"description\": \"Hint for the user. This will appear before user checks his answer/answers.\"\n              },\n              {\n                \"label\": \"Message displayed if answer is selected\",\n                \"description\": \"Message will appear below the answer on \\\"check\\\" if this answer is selected.\"\n              },\n              {\n                \"label\": \"Message displayed if answer is not selected\",\n                \"description\": \"Message will appear below the answer on \\\"check\\\" if this answer is not selected.\"\n              }\n            ]\n          }\n        ]\n      }\n    },\n    {\n      \"label\": \"Overall Feedback\",\n      \"fields\": [\n        {\n          \"widgets\": [\n            {\n              \"label\": \"Default\"\n            }\n          ],\n          \"label\": \"Define custom feedback for any score range\",\n          \"description\": \"Click the \\\"Add range\\\" button to add as many ranges as you need. Example: 0-20% Bad score, 21-91% Average Score, 91-100% Great Score!\",\n          \"entity\": \"range\",\n          \"field\": {\n            \"fields\": [\n              {\n                \"label\": \"Score Range\"\n              },\n              {},\n              {\n                \"label\": \"Feedback for defined score range\",\n                \"placeholder\": \"Fill in the feedback\"\n              }\n            ]\n          }\n        }\n      ]\n    },\n    {\n      \"label\": \"User interface translations for multichoice\",\n      \"fields\": [\n        {\n          \"label\": \"Check answer button label\",\n          \"default\": \"Check\"\n        },\n        {\n          \"label\": \"Show solution button label\",\n          \"default\": \"Show solution\"\n        },\n        {\n          \"label\": \"Retry button label\",\n          \"default\": \"Retry\"\n        },\n        {\n          \"label\": \"Tip label\",\n          \"default\": \"Show tip\"\n        },\n        {\n          \"label\": \"Textual representation of the score bar for those using a readspeaker\",\n          \"description\": \"Available variables are :num and :total\",\n          \"default\": \"You got :num out of :total points\"\n        },\n        {\n          \"label\": \"Tip Available (not displayed)\",\n          \"default\": \"Tip available\",\n          \"description\": \"Accessibility text used for readspeakers\"\n        },\n        {\n          \"label\": \"Feedback Available (not displayed)\",\n          \"default\": \"Feedback available\",\n          \"description\": \"Accessibility text used for readspeakers\"\n        },\n        {\n          \"label\": \"Read Feedback (not displayed)\",\n          \"default\": \"Read feedback\",\n          \"description\": \"Accessibility text used for readspeakers\"\n        },\n        {\n          \"label\": \"Wrong Answer (not displayed)\",\n          \"default\": \"Wrong answer\",\n          \"description\": \"Accessibility text used for readspeakers\"\n        },\n        {\n          \"label\": \"Correct Answer (not displayed)\",\n          \"default\": \"Correct answer\",\n          \"description\": \"Accessibility text used for readspeakers\"\n        },\n        {\n          \"label\": \"Option should have been checked\",\n          \"default\": \"Should have been checked\"\n        },\n        {\n          \"label\": \"Option should not have been checked\",\n          \"default\": \"Should not have been checked\"\n        },\n        {\n          \"label\": \"Text for \\\"Requires answer\\\" message\",\n          \"default\": \"Please answer before viewing the solution\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Behavioural settings\",\n      \"description\": \"These options will let you control how the task behaves.\",\n      \"fields\": [\n        {\n          \"label\": \"Enable \\\"Retry\\\" button\"\n        },\n        {\n          \"label\": \"Enable \\\"Show Solution\\\" button\"\n        },\n        {\n          \"label\": \"Enable \\\"Check\\\" button\"\n        },\n        {\n          \"label\": \"Question Type\",\n          \"description\": \"Select the look and behaviour of the question.\",\n          \"options\": [\n            {\n              \"label\": \"Automatic\"\n            },\n            {\n              \"label\": \"Multiple Choice (Checkboxes)\"\n            },\n            {\n              \"label\": \"Single Choice (Radio Buttons)\"\n            }\n          ]\n        },\n        {\n          \"label\": \"Give one point for the whole task\",\n          \"description\": \"Enable to give a total of one point for multiple correct answers. This will not be an option in \\\"Single answer\\\" mode.\"\n        },\n        {\n          \"label\": \"Randomize answers\",\n          \"description\": \"Enable to randomize the order of the answers on display.\"\n        },\n        {\n          \"label\": \"Require answer before the solution can be viewed\"\n        },\n        {\n          \"label\": \"Show confirmation dialog on \\\"Check\\\"\"\n        },\n        {\n          \"label\": \"Show confirmation dialog