INSERT INTO `h5p_libraries_languages` VALUES(7, 'it', '{\n  \"semantics\": [\n    {\n      \"label\": \"Media\",\n      \"fields\": [\n        {\n          \"label\": \"Tipo\",\n          \"description\": \"Media opzionale da mostrare sopra la domanda.\"\n        },\n        {\n          \"label\": \"Disabilita lo zoom dell\'immagine\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Domanda\"\n    },\n    {\n      \"label\": \"Opzioni disponibili\",\n      \"entity\": \"opzione\",\n      \"field\": {\n        \"label\": \"Opzione\",\n        \"fields\": [\n          {\n            \"label\": \"Testo\"\n          },\n          {\n            \"label\": \"Corretto\"\n          },\n          {\n            \"label\": \"Suggerimenti e feedback\",\n            \"fields\": [\n              {\n                \"label\": \"Testo del suggerimento\",\n                \"description\": \"Suggerimento per l\'utente. Apparirà prima l\'utente verifichi le sue risposte.\"\n              },\n              {\n                \"label\": \"Messaggio visualizzato se la risposta è selezionata\",\n                \"description\": \"Su \\\"Verifica\\\", apparirà un messaggio sotto la risposta se questa è selezionata\"\n              },\n              {\n                \"label\": \"Messaggio visualizzato se la risposta non è selezionata\",\n                \"description\": \"Su \\\"Verifica\\\", apparirà un messaggio sotto la risposta se questa non è selezionata\"\n              }\n            ]\n          }\n        ]\n      }\n    },\n    {\n      \"label\": \"Feedback generale\",\n      \"fields\": [\n        {\n          \"widgets\": [\n            {\n              \"label\": \"Predefinito\"\n            }\n          ],\n          \"label\": \"Definisci un feedback personalizzato per ogni intervallo di punteggio\",\n          \"description\": \"Esempio: 0-20%, cattivo punteggio; 21-91% punteggio media; 91-100% ottimo punteggio!\",\n          \"entity\": \"Intervallo\",\n          \"field\": {\n            \"fields\": [\n              {\n                \"label\": \"Intervallo di punteggio\"\n              },\n              {},\n              {\n                \"label\": \"Feedback per un intervallo di punteggio definito\",\n                \"placeholder\": \"Compila il feedback\"\n              }\n            ]\n          }\n        }\n      ]\n    },\n    {\n      \"label\": \"Traduzioni dell\'interfaccia utente per scelta multipla\",\n      \"fields\": [\n        {\n          \"label\": \"Etichetta del pulsante \\\"Verifica risposta\\\"\",\n          \"default\": \"Verifica\"\n        },\n        {\n          \"label\": \"Etichetta del pulsante \\\"Mostra soluzione\\\"\",\n          \"default\": \"Mostra la soluzione\"\n        },\n        {\n          \"label\": \"Etichetta del pulsante \\\"Riprova\\\"\",\n          \"default\": \"Riprova\"\n        },\n        {\n          \"label\": \"Etichetta del suggerimento\",\n          \"default\": \"Mostra suggerimento\"\n        },\n        {\n          \"label\": \"Rappresentazione testuale sulla barra del punteggio per chi usa un lettore vocale\",\n          \"description\": \"Le variabili disponibili sono :num e :total\",\n          \"default\": \"Il tuo punteggio è :num su :total\"\n        },\n        {\n          \"label\": \"Suggerimento disponibile (non visualizzato)\",\n          \"default\": \"Suggerimento disponibile\",\n          \"description\": \"Testo di accessibilità per riproduttori vocali\"\n        },\n        {\n          \"label\": \"Feedback disponibile (non visualizzato)\",\n          \"default\": \"Feedback disponibile\",\n          \"description\": \"Testo di accessibilità utilizzato per riproduttori vocali\"\n        },\n        {\n          \"label\": \"Leggi il feedback (non visualizzato)\",\n          \"default\": \"Leggi il feedback\",\n          \"description\": \"Testo di accessibilità utilizzato per riproduttori vocali\"\n        },\n        {\n          \"label\": \"Risposta sbagliata (non visualizzato)\",\n          \"default\": \"Risposta sbagliata\",\n          \"description\": \"Testo di accessibilità utilizzato per riproduttori vocali\"\n        },\n        {\n          \"label\": \"Risposta corretta (non visualizzato)\",\n          \"default\": \"Risposta corretta\",\n          \"description\": \"Testo di accessibilità utilizzato per riproduttori vocali\"\n        },\n        {\n          \"label\": \"L\'opzione avrebbe dovuto essere verificata\",\n          \"default\": \"Avrebbe dovuto essere verificato\"\n        },\n        {\n          \"label\": \"L\'opzione non avrebbe dovuto essere verificata\",\n          \"default\": \"Non avrebbe dovuto essere verificato\"\n        },\n        {\n          \"label\": \"Testo per il messaggio \\\"Richiede una risposta\\\"\",\n          \"default\": \"Per favore rispondi prima di vedere la soluzione\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Impostazioni esecuzione\",\n      \"description\": \"Queste opzioni ti consentono di controllare l\'esecuzione del compito.\",\n      \"fields\": [\n        {\n          \"label\": \"Attiva il pulsante Riprova\"\n        },\n        {\n          \"label\": \"Attiva il pulsante Mostra soluzione\"\n        },\n        {\n          \"label\": \"Attiva il pulsante \\\"Verifica\\\"\"\n        },\n        {\n          \"label\": \"Tipo di domanda\",\n          \"description\": \"Seleziona l\'aspetto e il comportamento della domanda.\",\n          \"options\": [\n            {\n              \"label\": \"Automatico\"\n            },\n            {\n              \"label\": \"Scelta multipla (caselle di controllo)\"\n            },\n            {\n              \"label\": \"Scelta singola (pulsanti di opzione)\"\n            }\n          ]\n        },\n        {\n          \"label\": \"Assegna un punto per l\'intero compito\",\n          \"description\": \"Attiva per assegnare un punto a risposte multiple corrette. Questa non è un\'opzione in modalità \\\"Risposta singola\\\".\"\n        },\n        {\n          \"label\": \"Risposte in ordine casuale\",\n          \"description\": \"Attiva per rendere casuale l\'ordine delle domande visualizzate.\"\n        },\n        {\n          \"label\": \"Richiede una risposta prima che la soluzione possa essere vista\"\n        },\n        {\n          \"label\": \"Mostra la finestra di dialogo di conferma su \\\"Verifica\\\"\"\n        },\n        {\n          \"label\": \"Mostra la finestra di dialogo di conferma su \\\"Riprova\\\"\"\n        },\n        {\n          \"label\": \"Controlla automaticamente le risposte\",\n          \"description\": \"Attivare questa opzione renderà difficoltosa l\'accessibilità. Assicurati di cosa stai facendo.\"\n        },\n        {\n          \"label\": \"Percentuale di superamento della prova\",\n          \"description\": \"Questa impostazione spesso non avrà alcun effetto. È la percentuale del punteggio totale necessario per ottenere un punto in caso di attività che valgano complessivamente un punto, e per ottenere result.success in dichiarazioni xAPI.\"\n        },\n        {\n          \"label\": \"Visualizza punteggio\",\n          \"description\": \"Visualizza i punti guadagnati per ogni risposta\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Finestra di conferma per la verifica\",\n      \"fields\": [\n        {\n          \"label\": \"Testo dell\'intestazione\",\n          \"default\": \"Finito?\"\n        },\n        {\n          \"label\": \"Testo del corpo\",\n          \"default\": \"Sei sicuro di voler concludere?\"\n        },\n        {\n          \"label\": \"Etichetta del pulsante di annullamento\",\n          \"default\": \"Annulla\"\n        },\n        {\n          \"label\": \"Etichetta del pulsante di conferma\",\n          \"default\": \"Finito\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Finestra di conferma per \\\"Riprova\\\"\",\n      \"fields\": [\n        {\n          \"label\": \"Testo dell\'intestazione\",\n          \"default\": \"Vuoi riprovare?\"\n        },\n        {\n          \"label\": \"Testo del corpo\",\n          \"default\": \"Sei sicuro di voler riprovare?\"\n        },\n        {\n          \"label\": \"Etichetta del pulsante di annullamento\",\n          \"default\": \"Annulla\"\n        },\n        {\n          \"label\": \"Etichetta del pulsante di conferma\",\n          \"default\": \"Conferma\"\n        }\n      ]\n    }\n  ]\n}\n');
INSERT INTO `h5p_libraries_languages` VALUES(7, 'ja', '{\n  \"semantics\": [\n    {\n      \"label\": \"メディア\",\n      \"fields\": [\n        {\n          \"label\": \"タイプ\",\n          \"description\": \"設問の上に表示するオプションのメディア。\"\n        },\n        {\n          \"label\": \"画像のズーム機能を無効化\"\n        }\n      ]\n    },\n    {\n      \"label\": \"設問\"\n    },\n    {\n      \"label\": \"使用可能なオプション\",\n      \"entity\": \"オプション\",\n      \"field\": {\n        \"label\": \"オプション\",\n        \"fields\": [\n          {\n            \"label\": \"テキスト\"\n          },\n          {\n            \"label\": \"正解\"\n          },\n          {\n            \"label\": \"ヒントとフィードバック\",\n            \"fields\": [\n              {\n                \"label\": \"ヒントのテキスト\",\n                \"description\": \"ユーザーのためのヒント。これはユーザーが答えをチェックする前に表示されます。\"\n              },\n              {\n                \"label\": \"答えを選択した場合に表示されるメッセージ\",\n                \"description\": \"この答えが選択された場合、「チェック」した答えの下にメッセージが表示されます。\"\n              },\n              {\n                \"label\": \"答えが選択されなかった場合に表示されるメッセージ\",\n                \"description\": \"この答えが選択されなかった場合、「チェック」した答えの下にメッセージが表示されます。\"\n              }\n            ]\n          }\n        ]\n      }\n    },\n    {\n      \"label\": \"全体のフィードバック\",\n      \"fields\": [\n        {\n          \"widgets\": [\n            {\n              \"label\": \"デフォルト\"\n            }\n          ],\n          \"label\": \"ある得点範囲に対して、カスタムフィードバックを定義します。\",\n          \"description\": \"例: 0-20% 低得点、 21-91% 平均点、 91-100% 高得点 !\",\n          \"entity\": \"範囲\",\n          \"field\": {\n            \"fields\": [\n              {\n                \"label\": \"得点範囲\"\n              },\n              {},\n              {\n                \"label\": \"定義した得点範囲に対するフィードバック\",\n                \"placeholder\": \"フィードバックを記入\"\n              }\n            ]\n          }\n        }\n      ]\n    },\n    {\n      \"label\": \"多肢選択のユーザーインターフェイスの翻訳\",\n      \"fields\": [\n        {\n          \"label\": \"答えをチェックするボタンのラベル\",\n          \"default\": \"チェック\"\n        },\n        {\n          \"label\": \"解を表示ボタンのラベル\",\n          \"default\": \"解を表示\"\n        },\n        {\n          \"label\": \"リトライボタンのラベル\",\n          \"default\": \"リトライ\"\n        },\n        {\n          \"label\": \"ヒントラベル\",\n          \"default\": \"ヒントを表示\"\n        },\n        {\n          \"label\": \"Textual representation of the score bar for those using a readspeaker\",\n          \"description\": \"Available variables are :num and :total\",\n          \"default\": \"You got :num out of :total points\"\n        },\n        {\n          \"label\": \"ヒント利用可能 (表示されません)\",\n          \"default\": \"ヒント利用可能\",\n          \"description\": \"Readspeakerに使用するアクセシビリティテキスト\"\n        },\n        {\n          \"label\": \"フィードバック利用可能 (表示されません)\",\n          \"default\": \"フィードバック利用可能\",\n          \"description\": \"Readspeakerに使用するアクセシビリティテキスト\"\n        },\n        {\n          \"label\": \"読みフィードバック(表示されません)\",\n          \"default\": \"読みフィードバック\",\n          \"description\": \"Readspeakerに使用するアクセシビリティテキスト\"\n        },\n        {\n          \"label\": \"不正解(表示されません)\",\n          \"default\": \"不正解\",\n          \"description\": \"Readspeakerに使用するアクセシビリティテキスト\"\n        },\n        {\n          \"label\": \"正解 (表示されません)\",\n          \"default\": \"正解\",\n          \"description\": \"readspeakerに使用するアクセシビリティテキスト\"\n        },\n        {\n          \"label\": \"オプションがチェックされている必要があります\",\n          \"default\": \"チェックされている必要があります\"\n        },\n        {\n          \"label\": \"オプションがチェックされていない必要があります\",\n          \"default\": \"チェックされていない必要があります\"\n        },\n        {\n          \"label\": \"「答えが必要」メッセージのテキスト\",\n          \"default\": \"解を表示する前に答えてください\"\n        }\n      ]\n    },\n    {\n      \"label\": \"動作設定\",\n      \"description\": \"これらのオプションでタスクがどのように動作するかを制御できます。\",\n      \"fields\": [\n        {\n          \"label\": \"「リトライ」ボタンを有効化\"\n        },\n        {\n          \"label\": \"「解を表示」ボタンを有効化\"\n        },\n        {\n          \"label\": \"Enable \\\"Check\\\" button\"\n        },\n        {\n          \"label\": \"問題タイプ\",\n          \"description\": \"外観と問題の動作を選択します。\",\n          \"options\": [\n            {\n              \"label\": \"自動\"\n            },\n            {\n              \"label\": \"複数選択(チェックボックス)\"\n            },\n            {\n              \"label\": \"択一 (ラジオボタン)\"\n            }\n          ]\n        },\n        {\n          \"label\": \"タスク全体に1点を与える\",\n          \"description\": \"複数の正解にトータルで1点与えることを有効化します。これは「択一解答」モードのオプションではありません。\"\n        },\n        {\n          \"label\": \"答えをランダム化\",\n          \"description\": \"表示される設問の順序をランダム化します。\"\n        },\n        {\n          \"label\": \"解を表示する前にユーザーの答えが必要\"\n        },\n        {\n          \"label\": \"「チェック」の確認ダイアログを表示\"\n        },\n        {\n          \"label\": \"「リトライ」の確認ダイアログを表示\"\n        },\n        {\n          \"label\": \"自動的に答えをチェック\",\n          \"description\": \"このオプションを有効にすると、アクセシビリティが損なわれます、行っていることを確認してください。\"\n        },\n        {\n          \"label\": \"合格パーセンテージ\",\n          \"description\": \"この設定は、多くの場合効果はありません。これは、タスク全体の1ポイントが有効になっているときに1ポイントを取得し、xAPIステートメントでresult.successを取得するために必要な合計得点の割合です。\"\n        },\n        {\n          \"label\": \"得点ポイントを表示\",\n          \"description\": \"それぞれの解答で得られたポイントを表示\"\n        }\n      ]\n    },\n    {\n      \"label\": \"チェック確認ダイアログ\",\n      \"fields\": [\n        {\n          \"label\": \"ヘッダーテキスト\",\n          \"default\": \"完了 ?\"\n        },\n        {\n          \"label\": \"本文テキスト\",\n          \"default\": \"本当に終了しますか ?\"\n        },\n        {\n          \"label\": \"キャンセルボタンのラベル\",\n          \"default\": \"キャンセル\"\n        },\n        {\n          \"label\": \"確認ボタンのラベル\",\n          \"default\": \"完了\"\n        }\n      ]\n    },\n    {\n      \"label\": \"リトライ確認ダイアログ\",\n      \"fields\": [\n        {\n          \"label\": \"ヘッダーテキスト\",\n          \"default\": \"リトライ ?\"\n        },\n        {\n          \"label\": \"本文テキスト\",\n          \"default\": \"本当にリトライしますか ?\"\n        },\n        {\n          \"label\": \"キャンセルボタンのラベル\",\n          \"default\": \"キャンセル\"\n        },\n        {\n          \"label\": \"確認ボタンのラベル\",\n          \"default\": \"確認\"\n        }\n      ]\n    }\n  ]\n}\n');
INSERT INTO `h5p_libraries_languages` VALUES(7, 'nb', '{\n  \"semantics\": [\n    {\n      \"label\": \"Multimedia\",\n      \"fields\": [\n        {\n          \"label\": \"Type\",\n          \"description\": \"En valgfri mediakilde som vises over spørsmålet.\"\n        },\n        {\n          \"label\": \"Disable image zooming\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Spørsmål\"\n    },\n    {\n      \"label\": \"Svaralternativer\",\n      \"entity\": \"verdi\",\n      \"field\": {\n        \"label\": \"Svaralternativ\",\n        \"fields\": [\n          {\n            \"label\": \"Tekst\"\n          },\n          {\n            \"label\": \"Riktig\"\n          },\n          {\n            \"label\": \"Tips\",\n            \"fields\": [\n              {\n                \"label\": \"Tipstekst\",\n                \"description\": \"Hint for brukeren. Dette vil vises før brukeren sjekker sine svar.\"\n              },\n              {\n                \"label\": \"Melding som vises hvis alternativet er valgt\",\n                \"description\": \"Melding som vises under svaret på \\\"check\\\" hvis svaret er valgt.\"\n              },\n              {\n                \"label\": \"Melding som vises hvis alternativet ikke er valgt\",\n                \"description\": \"Melding som vises under svaret på \\\"check\\\" hvis andre svar er valgt.\"\n              }\n            ]\n          }\n        ]\n      }\n    },\n    {\n      \"label\": \"Overall Feedback\",\n      \"fields\": [\n        {\n          \"widgets\": [\n            {\n              \"label\": \"Default\"\n            }\n          ],\n          \"label\": \"Define custom feedback for any score range\",\n          \"description\": \"Click the \\\"Add range\\\" button to add as many ranges as you need. Example: 0-20% Bad score, 21-91% Average Score, 91-100% Great Score!\",\n          \"entity\": \"range\",\n          \"field\": {\n            \"fields\": [\n              {\n              